import React, { useState } from 'react';
import './App.css';
import TextInput from './components/TextInput';
import Button from './components/Button';
import Container from './components/Container';

function App() {
  // Estados principales
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true); // Cambia entre login/register
  const [message, setMessage] = useState('');

  //  Validación de Email: debe contener "@" y terminar en ".com"
  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email) && email.endsWith('.com');
  };

  //  Validación de contraseña:
  // - Mínimo 6 caracteres
  // - Una mayúscula
  // - Un número
  // - Un carácter especial
  const validatePassword = (password) => {
    const isLongEnough = password.length >= 6;
    const hasUpperCase = /[A-Z]/.test(password);
    const hasNumber = /[0-9]/.test(password);
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);
    return isLongEnough && hasUpperCase && hasNumber && hasSpecialChar;
  };

  //  Maneja el envío del formulario
  const handleSubmit = () => {
    console.log(' Validando datos...');

    if (!validateEmail(email)) {
      console.log(' Email inválido');
      setMessage('El email debe contener "@" y terminar en ".com"');
      return;
    }

    if (!validatePassword(password)) {
      console.log(' Contraseña no cumple los requisitos');
      setMessage(
        'La contraseña debe tener al menos 6 caracteres, una mayúscula, un número y un carácter especial.'
      );
      return;
    }

    if (!isLogin) {
      // Registro y comprueba las contraseñas
      if (password !== confirmPassword) {
        console.log(' Las contraseñas no coinciden');
        setMessage('Las contraseñas no coinciden');
        return;
      }

      console.log(' Registro exitoso');
      setMessage('¡Registro exitoso!');
    } else {
      // Login
      console.log(' Login exitoso');
      setMessage('¡Login exitoso!');
    }
  };

  //  Cambia entre Login y Register
  const toggleForm = () => {
    setIsLogin(!isLogin);
    setMessage('');
    setEmail('');
    setPassword('');
    setConfirmPassword('');
    console.log(` Cambiando a vista: ${!isLogin ? 'Login' : 'Registro'}`);
  };

  return (
    <Container>
      <h2>{isLogin ? 'Iniciar Sesión' : 'Registrarse'}</h2>

      <TextInput
        label="Correo Electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type="email"
        name="email"
      />

      <TextInput
        label="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type="password"
        name="password"
      />

      {!isLogin && (
        <TextInput
          label="Confirmar Contraseña"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          type="password"
          name="confirmPassword"
        />
      )}

      <Button label={isLogin ? 'Login' : 'Registrarse'} onClick={handleSubmit} />

      {message && <p style={{ color: 'green', marginTop: '10px' }}>{message}</p>}

      <button className="toggle-button" onClick={toggleForm}>
        {isLogin ? '¿No tienes cuenta? Regístrate' : '¿Ya tienes cuenta? Inicia sesión'}
      </button>
    </Container>
  );
}

export default App;
