import React, { useState } from 'react';
import './App.css'; // Aquí también importas el CSS directamente en este archivo
import TextInput from './TextInput';
import Button from './Button';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  // Validación de Email
  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email) && email.endsWith('.com');
  };

  // Validación de Contraseña
  const validatePassword = (password) => {
    const isLongEnough = password.length >= 6;
    const hasUpperCase = /[A-Z]/.test(password);
    const hasNumber = /[0-9]/.test(password);
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);
    return isLongEnough && hasUpperCase && hasNumber && hasSpecialChar;
  };

  // Manejo de Envío del formulario
  const handleSubmit = () => {
    console.log('Validando datos...');
    if (!validateEmail(email)) {
      console.log('Email inválido');
      setMessage('El email debe contener "@" y terminar en ".com"');
      return;
    }

    if (!validatePassword(password)) {
      console.log('Contraseña no cumple los requisitos');
      setMessage('La contraseña debe tener al menos 6 caracteres, una mayúscula, un número y un carácter especial.');
      return;
    }

    if (password !== confirmPassword) {
      console.log('Las contraseñas no coinciden');
      setMessage('Las contraseñas no coinciden');
      return;
    }

    console.log('Registro exitoso');
    setMessage('¡Registro exitoso!');
  };

  return (
    <div className="container">
      <h2>Registrarse</h2>

      <TextInput
        label="Correo Electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type="email"
        name="email"
      />

      <TextInput
        label="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type="password"
        name="password"
      />

      <TextInput
        label="Confirmar Contraseña"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        type="password"
        name="confirmPassword"
      />

      <Button label="Registrarse" onClick={handleSubmit} />

      {message && <p style={{ color: 'green', marginTop: '10px' }}>{message}</p>}

      <button className="toggle-button">
        ¿Ya tienes cuenta? Inicia sesión
      </button>
    </div>
  );
};

export default Register;
